    public static Respuesta getSolicitudesCampanitaAmarillo(ContextoHB contexto) {
        Respuesta respuesta = Respuesta.exito();
        List<Objeto> solicitudesBD;
        ApiResponse responseProcesos = null;

        try {
            solicitudesBD = SqlSucursalVirtual
                    .SPGeneric("get_cobis", null, contexto.idCobis(), null)
                    .objetos("registro").stream().filter(porSolicitudesAA).toList();
            if (solicitudesBD.isEmpty()) return Respuesta.error().set("detalle", "Sin solicitudes");
        } catch (Exception e) {
            return Respuesta.error();
        }

        for (Objeto solicitudBD : solicitudesBD) {
            String nroSolicitud = solicitudBD.string("nro_solicitud");
            if (!esGestionAbierta(solicitudBD.integer("estado_solicitud"))) {
                //Solicitud ya cerrada - con la grilla de estados podemos saber si es | A F (aprob) | D R ( rechazado)
                String evaluacionRiesgo = solicitudBD.integer("estado_solicitud") == 3 ? "APROBADO" : "RECHAZADO";
                Objeto gestion = getGestion(contexto, solicitudBD, evaluacionRiesgo);
                Objeto notificacion = getNotificacion(solicitudBD);
                respuesta.add("gestionesCerradas", gestion);
                respuesta.add("notificaciones", notificacion);
                continue;
            }
            Boolean todosAceptaron = todosAceptaron(nroSolicitud);
            if (todosAceptaron) {
                String estadoApiVenta = "";
                ApiResponse ventaResponse = RestOmnicanalidad.consultarSolicitud(contexto, solicitudBD.string("nro_solicitud"));
                if (hayErrorPostVenta(ventaResponse))
                    return Respuesta.error().set("detalle", "consultarApiVentasSolcitud");

                //El estado en solicitudesGET puede ser un String o un Objeto
                if (ventaResponse.objetos("Datos").get(0).get("Estado") instanceof String) {
                    estadoApiVenta = ventaResponse.objetos("Datos").get(0).string("Estado");
                }
                if (ventaResponse.objetos("Datos").get(0).get("Estado") instanceof Objeto) {
                    estadoApiVenta = ventaResponse.objetos("Datos").get(0).objeto("Estado").string("Id");
                }
                //Todo: Hay un estado C... que significa?
                switch (estadoApiVenta) {
                    case "P" -> {
                        Respuesta respSQL = SqlSucursalVirtual.SPGeneric("get_nroSolicitud", nroSolicitud, null, null);
                        Boolean todosAceptaronNuevaPropuesta = respSQL
                                .objetos("registro")
                                .stream()
                                .allMatch(registro -> registro.bool("acepta_nueva_propuesta"));
                        Date fechaUltimoAceptado = getFechaAceptadoUltimo(respSQL.objetos("registro"));
                        Boolean solicitudVencida = esFechaVencidaAA(fechaUltimoAceptado, agregarDias(fechaUltimoAceptado, 5));
                        if(todosAceptaronNuevaPropuesta && Boolean.TRUE.equals(solicitudVencida)){
                            //Si pasaron +5 dias desde que el ultimo acepto la nueva propuesta, esta se vence.
                            SqlSucursalVirtual
                                    .SPGeneric("update_estado_solicitud", nroSolicitud, null, ESTADO_DESFAVORABLE_FECHA.toString())
                                    .objetos("registro")
                                    .stream()
                                    .filter(porNroSolicituBD(nroSolicitud).and(porCobis(contexto.idCobis())))
                                    .findFirst()
                                    .ifPresent(solicitudActualizadaDB -> {
                                                Objeto gestion = getGestion(contexto, solicitudActualizadaDB, "RECHAZADO");
                                                Objeto notificacion = getNotificacion(solicitudActualizadaDB);
                                                respuesta.add("gestionesCerradas", gestion);
                                                respuesta.add("notificaciones", notificacion);
                                            }
                                    );
                            continue;
                        }
                        if (responseProcesos == null) {
                            try {
                                responseProcesos = RestProcesos.getEstadoSolicitudes(contexto);
                                if (responseProcesos.hayError()) {
                                    return Respuesta.error().set("detalle", "getEstadoSolicitudes");
                                }
                            } catch (Exception e) {
                                return Respuesta.error().set("detalle", "getEstadoSolicitudes");
                            }
                        }
                        responseProcesos
                                .objetos("solicitudes")
                                .stream()
                                .filter(solicitud -> solicitud.string("idSolicitud").equalsIgnoreCase(nroSolicitud))
                                .findFirst()
                                .ifPresentOrElse(solBPM -> {
                                    //todo: Meter switch aca ya que api-ventas me dice P y BPM me dice F. Hay que validar el estado F que no esta contemplado aca.
                                    if (esAprobadoConModificaciones(solBPM.string("estado"))) {
                                        Objeto gestion = getGestion(contexto, solicitudBD, "APROB-MOD");
                                        Objeto notificacion = getNotificacion(solicitudBD);
                                        respuesta.add("gestionesEnCurso", gestion);
                                        respuesta.add("notificaciones", notificacion);
                                    } else {
                                        //PDICAR-PDUENOS SIGNIFICA QUE AUN ESTA EN REVISION?
                                        Objeto gestion = getGestion(contexto, solicitudBD, "REVISION");
                                        Objeto notificacion = getNotificacion(solicitudBD);
                                        respuesta.add("gestionesEnCurso", gestion);
                                        respuesta.add("notificaciones", notificacion);
                                    }
                                },()->{
                                    //Todo: Nuevo: Aceptamos la solicitud, en api-ventas cambio el estado a P pero en BPM no la econtramos. Es revision?
                                    Objeto gestion = getGestion(contexto, solicitudBD, "REVISION");
                                    Objeto notificacion = getNotificacion(solicitudBD);
                                    respuesta.add("gestionesEnCurso", gestion);
                                    respuesta.add("notificaciones", notificacion);
                                });

                    }
                    //Todo: Cuando acepto por primera vez y riesgo dictamina "FAVORABLE" cambia el estado en api-ventas?
                    case "A", "F" -> {
                        SqlSucursalVirtual
                                .SPGeneric("update_estado_solicitud", nroSolicitud, null, ESTADO_FAVORABLE.toString())
                                .objetos("registro")
                                .stream()
                                .filter(porNroSolicituBD(nroSolicitud).and(porCobis(contexto.idCobis())))
                                .findFirst()
                                .ifPresent(solicitudActualizadaDB -> {
                                            Objeto gestion = getGestion(contexto, solicitudActualizadaDB, "APROBADO");
                                            Objeto notificacion = getNotificacion(solicitudActualizadaDB);
                                            respuesta.add("gestionesCerradas", gestion);
                                            respuesta.add("notificaciones", notificacion);
                                        }
                                );

                    }
                    case "D", "R" -> {
                        SqlSucursalVirtual
                                .SPGeneric("update_estado_solicitud", nroSolicitud, null, ESTADO_DESFAVORABLE_SISTEMA.toString())
                                .objetos("registro")
                                .stream()
                                .filter(porNroSolicituBD(nroSolicitud).and(porCobis(contexto.idCobis())))
                                .findFirst()
                                .ifPresent(solicitudActualizadaDB -> {
                                            Objeto gestion = getGestion(contexto, solicitudActualizadaDB, "RECHAZADO");
                                            Objeto notificacion = getNotificacion(solicitudActualizadaDB);
                                            respuesta.add("gestionesCerradas", gestion);
                                            respuesta.add("notificaciones", notificacion);
                                        }
                                );
                    }
                    default -> {
                        //PIDE DOCUMENTACION
                        Objeto gestion = getGestion(contexto, solicitudBD, "REQ-DOC");
                        Objeto notificacion = getNotificacion(solicitudBD);
                        respuesta.add("gestionesEnCurso", gestion);
                        respuesta.add("notificaciones", notificacion);
                    }
                }
            } else {
                if (esSolicitudVencida(contexto, solicitudBD)) {
                    // Actualizo bd, del response obtengo el registro y filtro por mi numero de solicitud. // Armo gestion y notificacion con esta solicitud ya actualizada.
                    SqlSucursalVirtual
                            .SPGeneric("update_estado_solicitud", nroSolicitud, null, ESTADO_DESFAVORABLE_FECHA.toString())
                            .objetos("registro")
                            .stream()
                            .filter(porNroSolicituBD(nroSolicitud).and(porCobis(contexto.idCobis())))
                            .findFirst()
                            .ifPresent(solicitudActualizadaDB -> {
                                        Objeto gestion = getGestion(contexto, solicitudActualizadaDB, "");
                                        Objeto notificacion = getNotificacion(solicitudActualizadaDB);
                                        respuesta.add("gestionesCerradas", gestion);
                                        respuesta.add("notificaciones", notificacion);
                                    }
                            );
                    continue;
                }
                Objeto gestion = getGestion(contexto, solicitudBD, "");
                Objeto notificacion = getNotificacion(solicitudBD);
                respuesta.add("gestionesEnCurso", gestion);
                respuesta.add("notificaciones", notificacion);
                continue;
            }
        }
        return respuesta;
    }

    public static Respuesta getSolicitudesCampanitaVerde(ContextoHB contexto) {
        Respuesta respuesta = new Respuesta();
        List<Objeto> solicitudesAV;
        solicitudesAV = SqlSucursalVirtual
                .SPGeneric("get_cobis", null, contexto.idCobis(), null)
                .objetos("registro").stream().filter(porSolicitudesAV).toList();
        if (solicitudesAV.isEmpty()) return Respuesta.error().set("detalle", "Sin solicitudes");
        try {
            Objeto gestion;
            Objeto notificacion;
            for (Objeto solicitud : solicitudesAV) {
                gestion = new Objeto();
                notificacion = new Objeto();
                List<String> idsProductos = List.of(solicitud.string("id_producto").split("-"));
                Boolean esPrestamoPersona = idsProductos.stream().anyMatch(id -> id.equalsIgnoreCase(PRESTAMO_PERSONAL.getId()));
                Objeto detalle = new Objeto();
                if (esSolicitudVencida(contexto, solicitud)) {
                    Respuesta respApdate = SqlSucursalVirtual
                            .SPGeneric("update_estado_solicitud", solicitud.string("nro_solicitud"), null, ESTADO_DESFAVORABLE_FECHA.toString());
                    solicitud = respApdate.objetos("registro")
                            .stream()
                            .filter(r -> r.string("cobis").equalsIgnoreCase(contexto.idCobis()))
                            .findFirst()
                            .orElse(solicitud);
                }

                detalle = getDetalleOjito(contexto, solicitud);
                gestion.set("estadoGestion", getEstadoDesc(solicitud.string("estado_solicitud")));
                gestion.set("detalle", detalle.toMap());
                gestion.set("fecha", new SimpleDateFormat("dd/MM/yyyy").format(solicitud.date("fecha")));
                gestion.set("motivo", getMensajeProductos(solicitud.string("motivo")));
                gestion.set("producto", PRODUCTO.toString());
                gestion.set("numeroGestion", "SOL-" + solicitud.string("nro_solicitud"));
                gestion.set("esPrestamoPersonal", esPrestamoPersona);
                gestion.set("aceptado", solicitud.bool("aceptado"));
                gestion.set("productosPendientes", productosPendientes(idsProductos));

                if (solicitud.integer("estado_solicitud") <= GESTION_ABIERTA.toInteger()) {
                    respuesta.add("gestionesEnCurso", gestion);
                    notificacion.set("type", "SV-AV");
                    notificacion.set("dateDesc", new SimpleDateFormat("dd/MM/yyyy").format(solicitud.date("fecha")));
                    notificacion.set("read", solicitud.bool("leido"));
                    notificacion.set("estado", "ACTIVA");
                    notificacion.set("title", "Tu gestin \"" + getMensajeProductos(solicitud.string("motivo")) + "\" est activa");
                    notificacion.set("parte1", "Estamos analizando tu caso");
                    notificacion.set("numeroCaso", "SOL-" + solicitud.string("nro_solicitud"));
                    respuesta.add("notificaciones", notificacion);
                } else {
                    respuesta.add("gestionesCerradas", gestion);
                }
            }
        } catch (Exception e) {
            return Respuesta.error();
        }
        return respuesta;
    }

    public static Respuesta servicioCRMinsertGestion(ContextoHB contexto) {
        if (!ConfigHB.bool("hb_prendido_insert_sucursalvirtual")) {
            return Respuesta.error().set("detalle", "Service Off");
        }
        String nroSolicitud = contexto.parametros.string("nroSolicitud");
        String usuarioCrm = contexto.parametros.string("usuarioCrm", USUARIO_GENERICO.toString());
        String estadoGestion = contexto.parametros.string("estadoSolicitud", ESTADO_ACTIVA.toString());
        String resolucionId = contexto.parametros.string("resolucionId", "AV");
        String canal = contexto.parametros.string("canal");

        if (Objeto.anyEmpty(nroSolicitud, resolucionId)) {
            return Respuesta.parametrosIncorrectos();
        }

        ApiResponse respuestaApi = RestOmnicanalidad.consultarSolicitud(contexto, nroSolicitud);
        if (hayErrorPostVenta(respuestaApi)) {
            return Respuesta.error().set("detalle", "consultarApiVentasSolcitud");
        }
        List<Respuesta> respuestasDB = new ArrayList<>();
        List<Objeto> productos = respuestaApi.objetos("Datos").get(0).objetos("Productos");
        List<Objeto> integrantesNuevo = new ArrayList<>();

        String allProductos = productos
                .stream()
                .map(p -> p.string("Producto"))
                .collect(Collectors.joining("-"));

        String ids = productos
                .stream()
                .map(p -> p.string("IdProductoFrontEnd"))
                .collect(Collectors.joining("-"));

        respuestaApi
                .objetos("Datos")
                .get(0)
                .objetos("Integrantes")
                .stream()
                .filter(integrante -> integrante.string("Secuencia").equalsIgnoreCase("0"))
                .findFirst()
                .ifPresent(integrantesNuevo::add);

        ApiResponse apiResponse;
        for (Objeto producto : productos) {
            apiResponse = null;

            if (producto.string("IdProductoFrontEnd").equalsIgnoreCase(PAQUETE_PRODUCTO_NUEVO.getId())) {
                SolicitudPaquete solicitudPaquete = new SolicitudPaquete();
                try {
                    solicitudPaquete = SolicitudPaquete.get(contexto, nroSolicitud, producto.string("Id"));

                    List<SolicitudPaquete.ProductoExistente> productosExistente = solicitudPaquete.Paquete.ProductosExistentes;

                    SolicitudPaquete.ProductosNuevos productosNuevos = solicitudPaquete.Paquete.ProductosNuevos;
                    SolicitudTarjetaCredito tarjetaCredito = productosNuevos.TarjetaCredito; //Tarjeta de credito VISA (5)
                    SolicitudTarjetaDebito tarjetaDebito = productosNuevos.TarjetaDebito; //

                    SolicitudCuentaCorriente cuentaCorriente = productosNuevos.CuentaCorriente; // Cuenta corriente con acuerdo (7)
                    SolicitudCajaAhorro cajaAhorroPesos = productosNuevos.CajaAhorro; //
                    SolicitudCajaAhorro cajaAhorroDolares = productosNuevos.CajaAhorroDolares; //

                    if (tarjetaCredito != null) {
                        List<RolIntegrantes.RolIntegrante> integrantesTarjetaCredito = tarjetaCredito.Integrantes;
                        integrantesTarjetaCredito.forEach(integranteTarjetaCredito -> {
                            integrantesNuevo
                                    .stream()
                                    .filter(integranteNuevos -> integranteNuevos.string("NumeroDocumentoTributario").equalsIgnoreCase(integranteTarjetaCredito.NumeroDocumentoTributario))
                                    .findFirst()
                                    .ifPresentOrElse(i -> {
                                        //El integrante ya existe por lo tanto no hacemos nada.
                                    }, () -> {
                                        // El integrante no existe por lo tanto lo agregamos.
                                        Objeto aux = Objeto.fromJson(integranteTarjetaCredito.toJson());
                                        aux.set("Secuencia", 1);
                                        integrantesNuevo.add(aux);
                                    });
                        });
                    }

                    if (tarjetaDebito != null) {
                        List<RolIntegrantes.RolIntegrante> integrantesTarjetaDebito = tarjetaDebito.Integrantes;
                        integrantesTarjetaDebito.forEach(integranteCajaAhorro -> {
                            integrantesNuevo
                                    .stream()
                                    .filter(integranteNuevos -> integranteNuevos.string("NumeroDocumentoTributario").equalsIgnoreCase(integranteCajaAhorro.NumeroDocumentoTributario))
                                    .findFirst()
                                    .ifPresentOrElse(i -> {
                                        //El integrante ya existe por lo tanto no hacemos nada.
                                    }, () -> {
                                        // El integrante no existe por lo tanto lo agregamos.
                                        Objeto aux = Objeto.fromJson(integranteCajaAhorro.toJson());
                                        aux.set("Secuencia", 1);
                                        integrantesNuevo.add(aux);
                                    });
                        });
                    }

                    if (cuentaCorriente != null) {
                        List<RolIntegrante> integrantsCuentaCorriente = cuentaCorriente.getIntegrantes();
                        integrantsCuentaCorriente.forEach(integranteCajaAhorro -> {
                            integrantesNuevo
                                    .stream()
                                    .filter(integranteNuevos -> integranteNuevos.string("NumeroDocumentoTributario").equalsIgnoreCase(integranteCajaAhorro.NumeroDocumentoTributario))
                                    .findFirst()
                                    .ifPresentOrElse(i -> {
                                        //Si existe no hacemos nada - El integrante ya existe por lo tanto no hacemos nada.
                                    }, () -> {
                                        Objeto aux = Objeto.fromJson(integranteCajaAhorro.toJson());
                                        aux.set("Secuencia", 1);
                                        integrantesNuevo.add(aux);
                                    });
                        });
                    }

                    if (cajaAhorroPesos != null) {
                        List<RolIntegrantes.RolIntegrante> integrantesCajaAhorro = cajaAhorroPesos.Integrantes;
                        integrantesCajaAhorro.forEach(integranteCajaAhorro -> {
                            integrantesNuevo
                                    .stream()
                                    .filter(integranteNuevos -> integranteNuevos.string("NumeroDocumentoTributario").equalsIgnoreCase(integranteCajaAhorro.NumeroDocumentoTributario))
                                    .findFirst()
                                    .ifPresentOrElse(i -> {
                                        //El integrante ya existe por lo tanto no hacemos nada.
                                    }, () -> {
                                        // El integrante no existe por lo tanto lo agregamos.
                                        Objeto aux = Objeto.fromJson(integranteCajaAhorro.toJson());
                                        aux.set("Secuencia", 1);
                                        integrantesNuevo.add(aux);
                                    });
                        });
                    }

                    if (cajaAhorroDolares != null) {
                        List<RolIntegrantes.RolIntegrante> integrantesCajaAhorroDolares = cajaAhorroDolares.Integrantes;
                        integrantesCajaAhorroDolares.forEach(integranteTarjetaCredito -> {
                            integrantesNuevo
                                    .stream()
                                    .filter(integranteNuevos -> integranteNuevos.string("NumeroDocumentoTributario").equalsIgnoreCase(integranteTarjetaCredito.NumeroDocumentoTributario))
                                    .findFirst()
                                    .ifPresentOrElse(i -> {
                                        //El integrante ya existe por lo tanto no hacemos nada.
                                    }, () -> {
                                        // El integrante no existe por lo tanto lo agregamos.
                                        Objeto aux = Objeto.fromJson(integranteTarjetaCredito.toJson());
                                        aux.set("Secuencia", 1);
                                        integrantesNuevo.add(aux);
                                    });
                        });
                    }

                    productosExistente.forEach(productoExistente -> {
                        productoExistente.Integrantes
                                .forEach(integranteProductoExistente -> {
                                    integrantesNuevo
                                            .stream()
                                            .filter(integranteNuevo -> integranteNuevo.string("NumeroDocumentoTributario").equalsIgnoreCase(integranteProductoExistente.NumeroDocumentoTributario))
                                            .findFirst()
                                            .ifPresentOrElse(i -> {
                                                //El integrante ya existe por lo tanto no hacemos nada.
                                            }, () -> {
                                                // El integrante no existe por lo tanto lo agregamos.
                                                Objeto aux = Objeto.fromJson(integranteProductoExistente.toJson());
                                                aux.set("Secuencia", 1);
                                                integrantesNuevo.add(aux);
                                            });
                                });
                    });
                } catch (Exception e) {
                    return Respuesta.error();
                }
            }
            else{

                String tipoSolicitud = Productos.getTipoPorId(producto.string("IdProductoFrontEnd"));
                if(!tipoSolicitud.isEmpty()){
                    apiResponse = RestVenta.consultarSolicitudProducto(contexto, nroSolicitud, tipoSolicitud, producto.string("Id"));
                    if (hayErrorPostVenta(apiResponse)) {
                        return Respuesta.error().set("detalle", tipoSolicitud);
                    }

                    apiResponse
                            .objetos("Datos")
                            .get(0)
                            .objetos("Integrantes")
                            .forEach(integranteP -> {
                                integrantesNuevo
                                        .stream()
                                        .filter(integranteNuevo -> integranteNuevo.string("NumeroDocumentoTributario").equalsIgnoreCase(integranteP.string("NumeroDocumentoTributario")))
                                        .findFirst()
                                        .ifPresentOrElse(i -> {
                                            //Si existe no hacemos nada.
                                        }, () -> {
                                            // Seteamos Secuencia 1 para diferenciarlo del Titular | Ver solicitudesGET
                                            integranteP.set("Secuencia", 1);
                                            integrantesNuevo.add(integranteP);
                                        });

                            });
                }
            }
        }

        integrantesNuevo.forEach(integrante -> {
            Respuesta sqlResponse = SqlSucursalVirtual.insertGestionCRM(
                    integrante.string("IdCobis"),
                    estadoGestion,
                    new Date(),
                    nroSolicitud,
                    allProductos,
                    ids,
                    CERO.toInteger(),
                    integrante.integer("Secuencia"),
                    usuarioCrm,
                    canal,
                    integrante.string("Nombres") + " " + integrante.string("Apellido"),
                    integrante.string("NumeroDocumento"),
                    integrante.string("NumeroDocumentoTributario"),
                    resolucionId,
                    CERO.toInteger()
            );
            if (sqlResponse.hayError()) {
                respuestasDB.add(sqlResponse);
            }
        });

        if (respuestasDB.isEmpty()) {
            return Respuesta.exito();
        }
        return Respuesta.error().set("detalle", respuestasDB);
    }

    public static Respuesta datosConfirmacionProductos(ContextoHB contexto) {
        String nroSolicitud = contexto.parametros.string("nroSolicitud");

        if (Objeto.anyEmpty(nroSolicitud)) {
            return Respuesta.parametrosIncorrectos();
        }
        boolean aceptado = false;

        Respuesta respSQL = SqlSucursalVirtual.SPGeneric("get_nroSolicitud", nroSolicitud, null, null);
        Objeto user = respSQL
                .objetos("registro")
                .stream()
                .filter(reg -> reg.string("cobis").equalsIgnoreCase(contexto.idCobis()))
                .findFirst().orElse(null);
        if (user != null) {
            aceptado = user.bool("aceptado");
        }

        List<Objeto> integrantes = new ArrayList<>();
        Objeto registro;
        for (Objeto obj : respSQL.objetos("registro")) {
            registro = new Objeto();
            registro.set("cobis", obj.string("cobis"));
            registro.set("estadoSolicitud", obj.string("estado_solicitud"));
            registro.set("fechaAceptado", new SimpleDateFormat("dd/MM/yyyy").format(obj.date("fecha_aceptado")));
            registro.set("nombreCompleto", obj.string("nombre_completo"));
            registro.set("documento", obj.string("documento"));
            registro.set("fecha", new SimpleDateFormat("dd/MM/yyyy").format(obj.date("fecha_aceptado")));
            registro.set("nroSolicitud", obj.string("nro_solicitud"));
            registro.set("aceptado", obj.bool("aceptado"));
            registro.set("titularidad", obj.string("titularidad"));
            integrantes.add(registro);
        }

        Function<String, Objeto> stringToObjecto = producto -> new Objeto().set("nombre", producto);
        List<Objeto> productos = Arrays.stream(respSQL.objetos("registro")
                        .stream()
                        .findFirst()
                        .get()
                        .string("motivo")
                        .split("-"))
                .map(stringToObjecto)
                .toList();

        Respuesta resp = Respuesta.exito();
        resp.set("aceptado", aceptado);
        resp.set("integrantes", integrantes);
        resp.set("productos", productos);
        return resp;
    }

    public static Respuesta verTerminosYCondiciones(ContextoHB contexto) {
        try {
            String base64 = "";
            if (ConfigHB.esDesarrollo()) {
                base64 = pdfPredeterminado;
            } else {
                String nroSolicitud = contexto.parametros.string("nroSolicitud");
                ApiResponse response = RestSucursalVirtual.getTerminosYCondiciones(nroSolicitud, false, contexto);
                if (response.hayError()) {
                    contexto.responseHeader("estado", "ERROR");
                    return Respuesta.error();
                }
                base64 = response.string("Data");
            }
            try {
                byte[] archivo = Base64.getDecoder().decode(base64);
                return Respuesta.exito().set("archivo", archivo);
            } catch (Exception e) {
                contexto.responseHeader("estado", "ERROR");
                return Respuesta.error();
            }
        } catch (Exception e) {
            return Respuesta.error();
        }
    }

    public static Respuesta aceptarNuevaPropuesta(ContextoHB contexto) {
        //Todo: add servicio rollback en caso de error
        String nroSolicitud = contexto.parametros.string("nroSolicitud");

        if (Objeto.anyEmpty(nroSolicitud)) return Respuesta.parametrosIncorrectos();
        contexto.parametros.set("idSolicitud", nroSolicitud);

        Respuesta respuestaAceptacion = SqlSucursalVirtual.SPGeneric("acepta_nueva_propuesta", nroSolicitud, contexto.idCobis(), null);
        Boolean todosAceptaron = respuestaAceptacion.objetos("registro")
                .stream()
                .allMatch(registro -> registro.bool("acepta_nueva_propuesta"));

        Respuesta respFinalizar = null;
        if (todosAceptaron) {

            ApiResponse responseTyC = RestSucursalVirtual.getTerminosYCondiciones(nroSolicitud, true, contexto);
            if (responseTyC.hayError()) {
                SqlSucursalVirtual.SPGeneric("rollback_nueva_propuesta", nroSolicitud, contexto.idCobis(), null);
                return Respuesta.error();
            }

            respFinalizar = HBProcrearRefaccion.postFinalizar(contexto);
            if (respFinalizar.hayError()) {
                SqlSucursalVirtual.SPGeneric("rollback_nueva_propuesta", nroSolicitud, contexto.idCobis(), null);
                return Respuesta.error();
            }
            SqlSucursalVirtual.SPGeneric("update_estado_solicitud", nroSolicitud, null, ESTADO_FAVORABLE.toString());
        }
        return Respuesta.exito().set("pendiente", getIntegrantePendienteAceptacion(respuestaAceptacion.objetos("registro"), "acepta_nueva_propuesta"));
    }

    public static Respuesta aceptarSolicitud(ContextoHB contexto) {
        String nroSolicitud = contexto.parametros.string("nroSolicitud");

        if (Objeto.anyEmpty(nroSolicitud)) return Respuesta.parametrosIncorrectos();

//        Respuesta solicitud = SqlSucursalVirtual.buscarPorNroSolicitud(nroSolicitud);
        Respuesta solicitud = SqlSucursalVirtual.SPGeneric("get_nroSolicitud", nroSolicitud, null, null);
        if (solicitud.hayError()) {
            return Respuesta.error();
        }
        List<String> idsProductos = List.of(solicitud.objetos("registro").stream().findAny().get().string("id_producto").split("-"));

        for(String id : idsProductos){
            if(id.equalsIgnoreCase(PRESTAMO_PERSONAL.getId())){
                return Respuesta.error();
            }
        }

        Boolean solicitudVencida = esSolicitudVencida(contexto, solicitud.objetos("registro").stream().findAny().get());
        if (solicitudVencida) {
            return Respuesta.error().set("detalle", "Solicitud vencida");
        }
        try {
            Respuesta respuestaAceptacion = SqlSucursalVirtual.SPGeneric("aceptar_solicitud", nroSolicitud, contexto.idCobis(), null);

            if (respuestaAceptacion.hayError()) {
                return Respuesta.error();
            }
            if (todosAceptaron(nroSolicitud)) {
                Objeto titular = respuestaAceptacion.objetos("registro").stream().filter(porTitular).findFirst().orElse(null);
                if (titular == null) {
                    return Respuesta.error();
                }
                if (!ConfigHB.esDesarrollo()) {
                    ApiResponse responseTyC = RestSucursalVirtual.getTerminosYCondiciones(nroSolicitud, true, contexto);
                    if (responseTyC.hayError()) {
                        SqlSucursalVirtual.SPGeneric("rollback_aceptacion", nroSolicitud, contexto.idCobis(), null);
                        return Respuesta.error();
                    }
                }
                ApiResponse responseFinalizarSolicitud = RestSucursalVirtual.finalizarGestionCrm(contexto, titular.string("cobis"), titular.string("cuit"), "Favorable", nroSolicitud);
                if (responseFinalizarSolicitud.hayError() || !responseFinalizarSolicitud.objetos("Errores").isEmpty()) {
                    SqlSucursalVirtual.SPGeneric("rollback_aceptacion", nroSolicitud, contexto.idCobis(), null);

                    return Respuesta.error();
                }
                //Si la resolucion es aprobado verde no actualizamos a favorables ya que debemos esperar el estado de BPM
                if (titular.string("resolucion_id").equalsIgnoreCase("AV")) {
                    SqlSucursalVirtual.SPGeneric("update_estado_solicitud", nroSolicitud, null, ESTADO_FAVORABLE.toString());
                }
                return Respuesta.exito().set("pendiente", getIntegrantePendienteAceptacion(respuestaAceptacion.objetos("registro"), "aceptado"));
            } else {
                return Respuesta.exito().set("pendiente", getIntegrantePendienteAceptacion(respuestaAceptacion.objetos("registro"), "aceptado"));
            }
        } catch (Exception e) {
            SqlSucursalVirtual.SPGeneric("rollback_aceptacion", nroSolicitud, contexto.idCobis(), null);
            return Respuesta.error();
        }
    }

    public static Respuesta verNuevaPropuesta(ContextoHB contexto) {
        String nroSolicitud = contexto.parametros.string("nroSolicitud");

        Respuesta respuesta = Respuesta.exito();
        Respuesta solicitud = SqlSucursalVirtual.SPGeneric("get_nroSolicitud", nroSolicitud, null, null);

        if (solicitud.hayError()) return Respuesta.error();

        Date fechaVencimiento = getFechaAceptadoUltimo(solicitud.objetos("registro"));

        ApiResponse respuestaApiVentas = null;
        try {
            respuestaApiVentas = RestOmnicanalidad.consultarSolicitud(contexto, nroSolicitud);
            if (hayErrorPostVenta(respuestaApiVentas)) {
                return Respuesta.error().set("detalle", "consultarApiVentasSolcitud");
            }
        } catch (Exception e) {
            return Respuesta.error().set("detalle", "consultarApiVentasSolcitud");
        }
        List<Objeto> productos = respuestaApiVentas.objetos("Datos").get(0).objetos("Productos");
        Objeto paqueteProducto = productos
                .stream()
                .filter(porObjetoIdPaqueteProducto)
                .findFirst()
                .orElse(null);

        SolicitudPaquete responseSolicitudPaquete;
        List<Objeto> productosModificaciones = new ArrayList<>();
        Objeto productoAux;
        if (!Objeto.anyEmpty(paqueteProducto)) {
            try {

                responseSolicitudPaquete = SolicitudPaquete.get(contexto, nroSolicitud, paqueteProducto.string("Id"));
                SolicitudPaquete.ProductosNuevos productosNuevos = responseSolicitudPaquete.Paquete.ProductosNuevos;

                // PP PrestamoPersonal no entra en paquete de productos!
                SolicitudTarjetaCredito tarjetaCreditoPaquete = productosNuevos.TarjetaCredito; //Tarjeta de credito VISA (5)
                SolicitudCuentaCorriente cuentaCorrientePaquete = productosNuevos.CuentaCorriente; // Cuenta corriente con acuerdo (7)

                productos
                        .removeIf(producto -> producto.string("IdProductoFrontEnd").equalsIgnoreCase(PAQUETE_PRODUCTO_NUEVO.getId()));

                if (!Objeto.anyEmpty(cuentaCorrientePaquete)) {
                    productos.removeIf(producto -> producto.string("Id").equalsIgnoreCase(CUENTA_CORRIENTE_ACUERDO.getId()));
                    if(cuentaCorrientePaquete.getAcuerdo() != null){
                        productoAux = new Objeto();
                        productoAux.set("nombre", "Acuerdo en cuenta corriente");
                        productoAux.set("valor", Formateador.importe(cuentaCorrientePaquete.getAcuerdo().ValorAutorizado));
                        productosModificaciones.add(productoAux);
                    }
                }

                if (!Objeto.anyEmpty(tarjetaCreditoPaquete)) { // (5) tarjetaCredito
                    productos.removeIf(producto -> producto.string("IdProductoFrontEnd").equalsIgnoreCase(TARJETA_CREDITO_VISA.getId()));
                    productoAux = new Objeto();
                    productoAux.set("nombre", "Lmite Tarjeta de Crdito");
                    productoAux.set("valor", Formateador.importe(tarjetaCreditoPaquete.Limite));
                    productosModificaciones.add(productoAux);
                }
            } catch (Exception e) {
                return Respuesta.error().set("detalle", "SolicitudPaquete");
            }
        }

        if (!productos.isEmpty()) {
            Optional<Objeto> prestamoPersonal = productos
                    .stream()
                    .filter(p -> p.string("IdProductoFrontEnd").equalsIgnoreCase(PRESTAMO_PERSONAL.getId()))
                    .findFirst();
            if (prestamoPersonal.isPresent()) {
                try {
                    ApiResponse ppResponse = RestSucursalVirtual.prestamoPersonalGET(contexto, nroSolicitud, prestamoPersonal.get().string("Id"));
                    if (!(ppResponse.hayError() || !ppResponse.objetos("Errores").isEmpty())) {
                        Objeto pp = ppResponse.objetos("Datos").get(0);
                        productoAux = new Objeto();
                        productoAux.set("nombre", "Monto Prstamo Personal");
                        productoAux.set("valor", Formateador.importe(pp.bigDecimal("MontoAprobado")));
                        productosModificaciones.add(productoAux);
                    }
                } catch (Exception e) {
                }
            }
        }
        respuesta.set("fechaVencimiento", agregarDias(fechaVencimiento, 5));
        respuesta.set("modificaciones", productosModificaciones);
        return respuesta;
    }


