    public static Objeto getIntegrantePendienteAceptacion(List<Objeto> response, String atributo) {
        Objeto pendientes = new Objeto().set("titular", true).set("cotitular", true);
        response
                .stream()
                .filter(porTitular)
                .findFirst()
                .ifPresent((titular) -> {
                    if (titular.bool(atributo)) {
                        pendientes.set("titular", false);
                    }
                });
        Boolean cotitularAceptado = response
                .stream()
                .filter(porTitular.negate())
                .allMatch(cotitular -> cotitular.bool(atributo));
        pendientes.set("cotitular", !cotitularAceptado);
        return pendientes;
    }

    public static boolean esFechaVencida(Date fecha) {
        LocalDate fechaConvertida = fecha.toInstant()
                .atZone(ZoneId.systemDefault())
                .toLocalDate();
        LocalDate hoy = LocalDate.now();
        boolean valor = fechaConvertida.equals(hoy);
        return !valor; //Si la fecha son iguales significa que no esta vencida
    }

