        detalle = getDetalleOjito(contexto, solicitudBD);
        gestion.set("estadoGestion", getEstadoDesc(solicitudBD.string("estado_solicitud")));
        gestion.set("evaluacionRiesgo", evaluacionRiesgo);
        gestion.set("detalle", detalle.toMap());
        gestion.set("fecha", new SimpleDateFormat("dd/MM/yyyy").format(solicitudBD.date("fecha")));
