    public static Boolean esFechaVencidaAA(Date fechaSolicitud, String fechaVencimiento) {
        SimpleDateFormat sdf = new SimpleDateFormat("dd-MM-yyyy");
        Date fencimientoAux;
        try {
            fencimientoAux = sdf.parse(fechaVencimiento);
        } catch (Exception e) {
            return null;
        }
        Calendar calendar = Calendar.getInstance();
        calendar.setTime(fencimientoAux);

        //Setteamos a la fecha de vencimiento el horario.
        calendar.set(Calendar.HOUR_OF_DAY, fechaSolicitud.getHours());  // Establecer hora
        calendar.set(Calendar.MINUTE, fechaSolicitud.getMinutes());       // Establecer minutos

        Date fechaVencimientoParse = calendar.getTime();
        return new Date().after(fechaVencimientoParse);
    }

    private static Boolean esAprobadoConModificaciones(String resolucionBPM) {
        if (resolucionBPM.equalsIgnoreCase("APROMODAR")) {
            return true;
        }
        if (resolucionBPM.equalsIgnoreCase("PCONFOP")) {
            return true;
        }
        if (resolucionBPM.equalsIgnoreCase("PCONOPER")) {
            return true;
        }
        return false;
    }

    private static Boolean hayErrorPostVenta(ApiResponse response) {
        return response.hayError() || response.codigo == 204 || !response.objetos("Errores").isEmpty();
    }

    private static Boolean esGestionAbierta(Integer nroSolicitud) {
        return nroSolicitud <= GESTION_ABIERTA.toInteger();
    }

    public static Date getFechaAceptadoUltimo(List<Objeto> integrantes) {
        Optional<Objeto> solicitudMasReciente = integrantes.stream()
                .max(Comparator.comparing(s -> s.date("fecha_aceptado")));
        return solicitudMasReciente.map(objeto -> objeto.date("fecha_aceptado")).orElse(null);
    }

    public static String agregarDias(Date fecha, Integer dias) {
        Calendar calendar = Calendar.getInstance();
        calendar.setTime(fecha);
        calendar.add(Calendar.DAY_OF_MONTH, dias);
        SimpleDateFormat sdf = new SimpleDateFormat("dd-MM-yyyy");

        return sdf.format(calendar.getTime());
    }
